procedure.text:
BEGIN
    delete from rr_temp where skod = '56504_params_array';
    insert into rr_temp (skod, s1 , n1)
    select distinct '56504_params_array' as skod, 'BRANCH' AS s1, KOD_FL AS n1
    FROM jv_rep_cons_d_fl WHERE KOD_FL IN  :kod_fl ;
    insert into rr_temp (skod, s1 , n1)
    select distinct '56504_params_array' as skod, 'PWRC' AS s1, pwrc_id AS n1
    FROM hv_eopwrc WHERE PWRC_ID IN  :pwrc_id ;
    insert into rr_temp (skod, s1 , n1)
    select distinct '56504_params_array' as skod, 'V' AS s1, KOD_V AS n1
    FROM HK_V WHERE KOD_V IN  :kod_v ;

    JG_REPORTS_ISE.jr_rep_slpnt811_dtl3_load (ym_                => :ym,
                                              branch_array_id_   => 'BRANCH',
                                              pwrc_array_id_     => 'PWRC',
                                              vltg_array_id_     => 'V');
END;

