report.procedure:
DECLARE s_data_id rr_temp.skod%type; p_ym NUMBER; BEGIN s_data_id :='{3d8b60b1-72595-ac16-8c99c9d294fb}'; p_ym := :p_ym; delete vr_number_array; -- добавляем во временную таблицу данные по выбранному отделению INSERT INTO vr_number_array (array_id,val) values ('p_dep',:p_dep); -- добавляем фильтр по отделению INSERT INTO vr_number_array (array_id,val) values ('p_tipdog',0); -- нужны только договоры э/э и купли-продажи -- заполняем вр. таблицу rr_rep_dog данными по договорам в разрезе объектов kg_rep_dog.dog_obj_short;
-- Удаляем "договоры вне списка" DELETE rr_rep_dog where 1 = 0 {OR kod_dog in :p_no_dog};
DELETE FROM rr_temp WHERE skod = s_data_id; -- заполняем вр. таблицу rr_temp INSERT INTO rr_temp (skod, -- kod report n1, -- kodp для ТСО s1, -- tco_name n2, -- kod_dog s2, -- ndog n3, -- kod_numobj n4, -- numobj_num s3, -- numobj_name n5, -- max_power (максимальная мощность на объекте) n6, -- max_by_obj_per_period (максимальное числовое значение по объекту в течение месяца) n7, -- cust_kvth n8, -- cust_kvt n9, -- cust_kvt_set n10 -- kod_gtp ) SELECT s_data_id, max(tco.kodp) as kodp_tco , max(tco.name) as tco_name , max(a.kod_dog) as kod_dog , max(d.ndog) as ndog , a.kod_numobj , max(n.num_obj) as numobj_num , max(n.name) as numobj_name , max(COALESCE(HG_DOGR.get_PMAX_NUMOBJ(a.kod_numobj, 2, kg.ym_first_day(a.ym) ,0,null),0)) as max_power , max(ng_priem.f_max_potr_hour(a.kod_numobj,p_ym)) as max_by_obj_per_period -- максимальное числовое значение по объекту в течение месяца , sum(CASE WHEN s.edizm = 4 THEN COALESCE(cust,0) ELSE 0 END) as cust_kvth , sum(CASE WHEN a.vid_t = -272 THEN COALESCE(cust,0) ELSE 0 END) as cust_kvt , sum(CASE WHEN a.vid_t = -222 THEN COALESCE(cust,0) ELSE 0 END) as cust_kvt_set , max(COALESCE(hg_common.get_gtp_add_regl(a.kod_numobj, ym, 0),0)) as kod_gtp FROM rr_rep_dog d INNER JOIN nr_account a ON d.kod_numobj = a.kod_numobj -- предварительно таблица rr_rep_dog д.б. заполнена данными по нужным договорам с нужными фильтрами INNER JOIN TABLE (HG_SALEPOINT_R.PL_getSLPOrgs (kod_numobj_ => a.kod_numobj, ym_ => p_ym) ) pc ON (a.kod_numobj = pc.kod_numobj) LEFT JOIN kr_payer tco ON pc.kodp_pwrcompany = tco.kodp INNER JOIN sk_nachisl s ON a.vid_t = s.vid_t INNER JOIN hr_reglament_pp p ON a.kod_reglament = p.kod_reglament INNER JOIN hr_calc_variant v ON p.kod_variant = v.kod_variant LEFT JOIN kr_numobj n ON a.kod_numobj = n.kod_numobj WHERE a.ym = p_ym AND kod_typevariant = 0 -- продажа (Доп. регламент "Факт") GROUP BY a.kod_numobj ;
UPDATE rr_temp t SET (t.n11, -- pw as "генерирующая" (Мощность фактическая по общему профилю ГТП) t.n12 -- pw_set as "сетевая" (Мощность сетевая по общему профилю ГТП) ) = ( select pw, pw_set from nr_gtp_cost c inner join hs_gtp g ON g.XLS_GTP_ID = c.XLS_GTP_ID where pr_plan = 0 and ym = p_ym AND kod_gtp = t.n10 ) WHERE t.skod = s_data_id; END;
