table.insert-text:
begin for r in ( select :npp as npp ,:kod_titul_ip as kod_titul_ip ,:kod_ofz_tit_order as kod_ofz_tit_order
from dual )
loop insert into ipr_ofz_tit_order (npp ,kod_titul_ip ,kod_ofz_tit_order ) values (r.npp ,r.kod_titul_ip ,r.kod_ofz_tit_order ) returning kod_ofz_tit_order into :kod_ofz_tit_order; end loop; end;

table.update-temp-text:
begin delete from rr_temp where skod='a' and names=:form_id and f2=:kod_ofz_tit_order; if :row_state_id=1 then for r in ( select :npp as npp ,:kod_titul_ip as kod_titul_ip ,:kod_ofz_tit_order as kod_ofz_tit_order
from dual )
loop insert into rr_temp (skod ,names ,f2 ,f3 ,n1 ,n2 ,n3 ) values ('a' ,:form_id ,:kod_ofz_tit_order ,:row_state_id ,r.npp ,r.kod_titul_ip ,r.kod_ofz_tit_order ); end loop; else for r in ( select :npp as npp ,:kod_titul_ip as kod_titul_ip ,:kod_ofz_tit_order as kod_ofz_tit_order
from ipr_ofz_tit_order a where kod_ofz_tit_order=:kod_ofz_tit_order)
loop insert into rr_temp (skod ,names ,f2 ,f3 ,n1 ,n2 ,n3 ) values ('a' ,:form_id ,:kod_ofz_tit_order ,:row_state_id ,r.npp ,r.kod_titul_ip ,r.kod_ofz_tit_order ); end loop; end if; end;

table.update-text:
begin update ipr_ofz_tit_order set (npp ,kod_titul_ip ,kod_ofz_tit_order ) = ( select :npp as npp ,:kod_titul_ip as kod_titul_ip ,:kod_ofz_tit_order as kod_ofz_tit_order
from dual )
where kod_ofz_tit_order=:kod_ofz_tit_order ; end;
