table.insert-text:
begin for r in ( select :stage_num as stage_num ,:creditor_name as creditor_name ,:sum_main_claim as sum_main_claim ,:voice_percent as voice_percent ,:penalty as penalty ,:repay_claim as repay_claim ,:prim as prim ,:kod_cred_claim_reg as kod_cred_claim_reg ,:kod_folders as kod_folders
from dual )
loop insert into ur_creditors_claim_register (stage_num ,creditor_name ,sum_main_claim ,voice_percent ,penalty ,repay_claim ,prim ,kod_cred_claim_reg ,kod_folders ) values (r.stage_num ,r.creditor_name ,r.sum_main_claim ,r.voice_percent ,r.penalty ,r.repay_claim ,r.prim ,r.kod_cred_claim_reg ,r.kod_folders ) returning kod_cred_claim_reg into :kod_cred_claim_reg; end loop; end;

table.update-temp-text:
