loop insert into rr_temp (skod ,names ,f2 ,f3 ,n1 ,s1 ,n2 ,n3 ,n4 ,n5 ,s2 ,n6 ,n7 ,n8 ) values ('b1' ,:form_id ,:kod_cred_claim_reg ,:row_state_id ,r.stage_num ,r.creditor_name ,r.sum_main_claim ,r.voice_percent ,r.penalty ,r.repay_claim ,r.prim ,r.kod_cred_claim_reg ,r.kod_folders ,r.kod_cred_claim_status ); end loop; end if; end;

