DECLARE p_dep kr_dogovor.dep%type; p_date DATE; s_data_id rr_temp.skod%type; begin
p_dep := :dep; -- 20488 Оренбургский филиал p_date := :dat; s_data_id := '{E695CD85-3F22-4E80-9D0A-2667FAA4F97B}'; delete vr_number_array; insert into vr_number_array (array_id,val) values ('p_dep', p_dep);
/*Заполняем временную рабочую таблицу rr_rep_dz_kz. dep вставлен в таблицу vr_number_array при выборе параметра на форме */ sg_rep_dz_kz.dz_on_date_per_sf(p_date); sg_rep_dz_kz.kz_on_date_per_opl(p_date); -- delete rr_temp where s_data_id = '{E695CD85-3F22-4E80-9D0A-2667FAA4F97B}';
INSERT INTO rr_temp ( skod -- s_data_id - guid отчета , n1 -- kod_shc (1 для ДЗ) , n2 -- kod_dog , n3 -- kod_sf , s1 -- kod_be , s2 -- ndog /*Номер договора*/
, d1 -- dat_doc , d2 -- dat_bzad /*дата задолженности*/
, s3 -- abon_info /* Наименование дебитора/кредитора */ , s4 -- nom_sf , s5 -- name_shc (Для Гаранта "62.01" или "62.02") , s6 -- name_doc (сч/ф, пл.поручение,...)
, n4 -- inn , n5 -- deb /* дебиторская задолженность на дату*/ , n6 -- kred /* кредиторская задолженность на дату*/
) WITH pre as (SELECT kod_sch , kod_dog , kod_sf , nom_sf , dat_sf , dat_bzad , (nachisl - opl) As dz_on_date , name_doc , NULL as num_opl , NULL as dat_opl , NULL as opl_kz FROM rr_rep_dz_kz WHERE kod_sch = 1 UNION ALL SELECT kod_sch , kod_dog , NULL as kod_sf , NULL as nom_sf , NULL as dat_sf , dat_bzad , NULL as dz_on_date , name_doc , num_opl , dat_opl , opl_kz FROM rr_rep_dz_kz WHERE kod_sch = 3 )
SELECT s_data_id , pre.kod_sch as n1 , pre.kod_dog as n2 -- kod_dog , pre.kod_sf as n3 -- kod_sf , Case When d.dep = 1172 Then 'R089' When d.dep in (20488, 21626) Then 'R015' When d.dep in (20578, 20579, 21621) Then 'R088' When d.dep = 27299 Then 'R134' When d.dep = 21628 Then 'R135' When d.dep = 27301 Then 'R136' end As s1 -- kod_be , d.ndog || ' от ' || (to_char(d.dat_sign,'DD.MM.YYYY')) as s2 -- ndog /*Номер договора*/ , CASE WHEN pre.kod_sch =1 THEN pre.dat_sf ELSE pre.dat_opl END as d1 -- dat_doc , pre.dat_bzad as d2 -- dat_bzad /*дата задолженности*/
, d.ndog || ' ' || p.name as s3 -- abon_info /* Наименование дебитора */ , CASE WHEN pre.kod_sch=1 THEN pre.nom_sf ELSE to_char(num_opl) END as s4 -- num_doc , CASE WHEN kod_sch = 1 THEN '62.01' ELSE '62.02' END as s5 -- name_shc , pre.name_doc as s6 -- name_doc
, p.inn as n4 -- inn , nvl(pre.dz_on_date, 0) as n5 -- deb /* дебиторская задолженность на дату*/ , nvl(pre.opl_kz, 0) as n6 -- kred /* кредиторская задолженность на дату*/
From pre Inner Join kr_dogovor d On pre.kod_dog = d.kod_dog Left Join kr_org g On d.dep = g.kodp Left Join kr_payer p On d.kodp = p.kodp WHERE pre.dz_on_date > 0 or pre.opl_kz > 0 ;
end;

