report.procedure:
DECLARE p_kod_dog kr_dogovor.kod_dog%type; p_dep kr_dogovor.dep%type; p_ym nv_account_sost.ym%type; s_data_id rr_temp.skod%type; BEGIN p_dep := :dep; p_ym := :p_ym; p_kod_dog := :p_kod_dog; s_data_id := '{7D212244-454A-4FF2-8D40-0A4C2FED9A2E}'; -- DELETE FROM rr_temp WHERE skod = s_data_id; -- 1. Приёмки за энергию (кВт-ч) INSERT INTO rr_temp (skod, rn, n1, n2, n3, n4, n5, n6, n7, n10 /* is_2rate */, n11, s1, n12 /* object_kodd */, n13, n14, n15, n16, s2, n18 /* kod_pointmain */, s4, s5, n19, n20, n21, n22, n23, n24, n25, n26, n27, n28 /* kod_polbalans */, n29, n31 /* num_obj */, s6 /*src_dat_name - источник показаний (наименование)*/) SELECT s_data_id, ROW_NUMBER () OVER (PARTITION BY ac.kod_numobj, ac.voltage ORDER BY pr.nomer NULLS LAST, ac.kod_account) as rn, ac.kod_dog, ac.kod_account, ac.rym, ac.kod_numobj, ac.voltage, ac.kodinterval, ac.cust as energy, ac.is_2rate, no.kod_obj, no.name AS object_name, ob.kodd AS object_kodd, pr.kod_priem, pr.kod_point, pr.tpoint, pr.nomer AS point_nomer, pr.name AS point_name, NVL(pr.kod_pointmain_transit, pr.kod_pointmain) AS kod_pointmain, CASE WHEN pu.kod_tippu IS NOT NULL THEN hg_pasp_pu_r.name_pu(pu.kod_tippu, 2) ELSE '' END AS type_pu, pr.nom_pu, pr.readlast, pr.readprev, pr.outcounter, pr.outadd, pr.out, pr.rkoeff, pr.razr, pr.razr2, pr.kod_point_har, pr.kod_polbalans, 0 as power, no.num_obj, ng_report.get_src_dat_name(pr.kod_priem) FROM (SELECT a.kod_dog, a.kod_account, a.ym, a.rym, a.kod_numobj, a.kod_account_priem, a.voltage, a.kodinterval, a.cust, a.price, a.money, a.vid_t, kg_tarif.is_2rate(kg_tarif.subtarif_voltage(a.tarif, a.voltage, a.dat_priem - 1E-5), a.dat_priem - 1E-5) as is_2rate FROM kr_dogovor d JOIN nv_account_sost a ON d.kod_dog = a.kod_dog JOIN hr_reglament_pp p ON p.kod_reglament = a.kod_reglament JOIN hr_calc_variant v ON v.kod_variant = p.kod_variant WHERE d.dep = p_dep AND (p_kod_dog IS NULL OR d.kod_dog = p_kod_dog) AND a.ym = p_ym AND v.kod_typevariant = 1 AND v.pr_notransit = 1 AND a.price is not null AND a.vid_t != -222 AND a.tip_tarif_sost = 2 ) ac JOIN kr_numobj no ON no.kod_numobj = ac.kod_numobj JOIN kr_object ob ON ob.kod_obj = no.kod_obj LEFT OUTER JOIN nr_account_priem ac_pr ON ac_pr.kod_account_priem = ac.kod_account_priem LEFT OUTER JOIN nv_priem pr ON pr.kod_priem = ac_pr.kod_priem LEFT OUTER JOIN hr_point_pu pu ON pu.kod_point_pu = pr.kod_point_pu;
-- 2. Приёмки за энергию (кВт в n29) FOR cur IN (SELECT a.kod_dog, a.kod_account, a.rym, a.kod_numobj, a.kod_account_priem, a.voltage, a.kodinterval, a.cust, a.price, a.money, a.vid_t, kg_tarif.is_2rate(kg_tarif.subtarif_voltage(a.tarif, a.voltage, a.dat_priem - 1E-5), a.dat_priem - 1E-5) as is_2rate, no.kod_obj, no.name AS object_name, ob.kodd AS object_kodd FROM kr_dogovor d JOIN nv_account_sost a ON d.kod_dog = a.kod_dog JOIN hr_reglament_pp p ON p.kod_reglament = a.kod_reglament JOIN hr_calc_variant v ON v.kod_variant = p.kod_variant JOIN kr_numobj no ON no.kod_numobj = a.kod_numobj JOIN kr_object ob ON ob.kod_obj = no.kod_obj WHERE d.dep = p_dep AND (p_kod_dog IS NULL OR d.kod_dog = p_kod_dog) AND a.ym = p_ym AND v.kod_typevariant = 1 AND v.pr_notransit = 1 AND a.price is not null AND a.vid_t = -222) LOOP UPDATE rr_temp SET n29 = n29 + NVL(cur.cust, 0) WHERE skod = s_data_id AND n4 = cur.kod_numobj AND rn = 1 AND n5 = cur.voltage; IF SQL%ROWCOUNT = 0 THEN INSERT INTO rr_temp (skod, rn, n1, n2, n3, n4, n5, n6, n7, n10 /* is_2rate */, n11, s1, n12 /* object_kodd */, n13, n14, n15, n16, s2, n18 /* kod_pointmain */, s4, s5, n19, n20, n21, n22, n23, n24, n25, n26, n27, n28 /* kod_polbalans */, n29) VALUES (s_data_id, 1, cur.kod_dog, cur.kod_account, cur.rym, cur.kod_numobj, cur.voltage, cur.kodinterval, NULL /* energy */, cur.is_2rate, cur.kod_obj, cur.object_name, cur.object_kodd, NULL /* kod_priem */, NULL /* kod_point */, NULL /* tpoint */, NULL /* point_nomer */, '' /* point_name */, NULL /* kod_pointmain */, '' /* type_pu */, '' /* nom_pu */, NULL /* readlast */, NULL /* readprev */, NULL /* outcounter */, NULL /* outadd */, NULL /* out */, NULL /* rkoeff */, NULL /* razr */, NULL /* razr2 */, NULL /* kod_point_har */, NULL /* kod_polbalans */, cur.cust); END IF; END LOOP;
-- 3. Проставляем kod_attpoint (в n30) UPDATE rr_temp SET n30 = CASE WHEN n14 IS NOT NULL THEN (SELECT pt.kod_attpoint FROM hr_point pt WHERE pt.kod_point = n14) ELSE (SELECT MAX(att.kod_attpoint) AS kod_attpoint FROM hr_nugruzpotreb nug JOIN hr_attpoint att ON att.kodnagruzpotreb = nug.kodnagruzpotreb WHERE nug.kod_obj = n11) END WHERE skod = s_data_id AND n14 IS NOT NULL;
-- 4. Внешний транзит INSERT INTO rr_temp (skod, rn, n1, n2, n3, n4, n5, n6, n7, n10 /* is_2rate */, n11, s1, n12 /* object_kodd */, n13, n14, n15, n16, s2, n18 /* kod_pointmain */, s4, s5, n19, n20, n21, n22, n23, n24, n25, n26, n27, n28 /* kod_polbalans */, n29, n30, n31 /* num_obj */, s6 /*src_dat_name - источник показаний (наименование)*/) SELECT s_data_id, ROW_NUMBER() OVER (PARTITION BY pr.kod_numobj ORDER BY pr.nomer NULLS LAST, pr.kod_priem) as rn, pr.kod_dog, NULL AS kod_account, NULL AS rym, no.kod_numobj, pr.voltage, 24 AS kodinterval, NULL AS energy, (SELECT NVL(MAX(t.n10),0) as is_2rate FROM rr_temp t WHERE t.skod = s_data_id AND t.n15 /*tpoint*/ != 3 AND t.n14 = pr.kod_pointmain) AS is_2rate, no.kod_obj, no.name AS object_name, ob.kodd AS object_kodd, pr.kod_priem, pr.kod_point, pr.tpoint, pr.nomer AS point_nomer, pr.name AS point_name, pr.kod_pointmain, CASE WHEN pu.kod_tippu IS NOT NULL THEN hg_pasp_pu_r.name_pu(pu.kod_tippu, 2) ELSE '' END AS type_pu, pr.nom_pu, pr.readlast, pr.readprev, pr.outcounter, pr.outadd, -pr.out, pr.rkoeff, pr.razr, pr.razr2, pr.kod_point_har, pr.kod_polbalans, 0 as power, pt.kod_attpoint, no.num_obj, ng_report.get_src_dat_name(pr.kod_priem) FROM kr_dogovor d JOIN nv_priem pr ON pr.kod_dog = d.kod_dog JOIN hr_point pt ON pt.kod_point = pr.kod_point JOIN kr_numobj no ON no.kod_numobj = pr.kod_numobj JOIN kr_object ob ON ob.kod_obj = no.kod_obj LEFT OUTER JOIN hr_point_pu pu ON pu.kod_point_pu = pr.kod_point_pu WHERE pr.tpoint = 3 AND d.dep = p_dep AND (p_kod_dog IS NULL OR d.kod_dog = p_kod_dog) AND pr.kod_pointmain IN (SELECT t.n14 as kod_point FROM rr_temp t WHERE t.skod = s_data_id AND t.n15 /*tpoint*/ != 3) AND pr.ym = p_ym; END;

procedure.text:
begin
  delete vr_number_array;
  for rec in( SELECT regexp_substr(str, '[^,]+', 1, level) KODP
    FROM (
          SELECT rtrim(ltrim(trim(':tso'),'('),')') str FROM dual     ) t
   where t.str is not null
    CONNECT BY instr(str, ',', 1, level - 1) > 0

)
 loop
insert into vr_number_array (array_id,val)
values ('tso',rec.kodp);
 end loop;



  for rec in( SELECT regexp_substr(str, '[^,]+', 1, level) KODP
    FROM (
          SELECT rtrim(ltrim(trim(':tso2'),'('),')') str FROM dual     ) t
   where t.str is not null
    CONNECT BY instr(str, ',', 1, level - 1) > 0

)
 loop
insert into vr_number_array (array_id,val)
values ('tso2',rec.kodp);
 end loop;



     sqlb_rep_ies.fill_table_64883_1(:p_date);
end;

declare     
    NO_SELECT EXCEPTION;
    t number := 0;
    t1 number := 0; 

begin

     delete vr_number_array;



   insert into vr_number_array (array_id,val) 
    select 'tso1', kodp
    from kr_org where 0=1 {or kodp in :tso1};


    select COUNT(array_id) into t from vr_number_array;

insert into vr_number_array (array_id,val)

    select 'tso2', kodp
   from kr_org where 0=1 {or kodp in :tso2};



select COUNT(array_id) into t1 from vr_number_array;



if (t = 0 AND t1 = 0)
THEN
    RAISE_APPLICATION_ERROR (-20001,'Выберите параметр отчета "ТСО для услуги" или "ТСО для потерь".');

else
     sqlb_rep_ies.fill_table_64883_1(:p_date);
end if; 

end;

