report.procedure:
DECLARE p_dep kr_dogovor.dep%type; p_year number; p_ym_beg nv_account.ym%type; p_ym_end nv_account.ym%type; s_data_id rr_temp.skod%type; BEGIN p_dep := :p_dep; p_year := :p_year; p_ym_beg := p_year + (:p_kv * 3 - 2) / 100; -- первый месяц квартала периода p_ym_end := p_year + (:p_kv * 3) / 100; -- последний месяц квартала периода s_data_id :='{3d8b60b1-6cd0-416a-ac16-8c99c9d294fb}';
DELETE FROM rr_temp WHERE skod = s_data_id;
INSERT INTO rr_temp (skod, n1) SELECT s_data_id, sum(CASE WHEN (nk_adress_r.get_oktmo_h(CASE WHEN ob.kodd IS NULL OR ob.kodd = -1 THEN d.kod_d_dog ELSE ob.kodd END)) = 61701000001 -- OКТМО г. Рязань THEN a.cust ELSE 0 END) as cust FROM nv_account_priem a INNER JOIN kr_dogovor d ON a.kod_dog=d.kod_dog INNER JOIN kr_dogovor dp On dp.kod_dog = d.kod_dog_fin LEFT JOIN kr_numobj no ON no.kod_numobj = a.kod_numobj LEFT JOIN kr_object ob ON ob.kod_obj = no.kod_obj LEFT JOIN sk_nachisl n ON a.vid_t=n.vid_t LEFT JOIN sk_vid_real vr ON n.vid_real = vr.vid_real LEFT JOIN sk_edizm e ON n.edizm = e.edizm WHERE a.ym BETWEEN p_ym_beg AND p_ym_end AND dp.dep = p_dep AND a.price is not null AND vr.pr_osn = 1 -- основная реализация AND e.kod_okei = '245' -- kvth ; END;
