report.procedure:
BEGIN
delete vr_number_array; insert into vr_number_array (array_id,val) values ('p_dep',:p_dep); -- Получаем краткую информацию по договорам отделения kg_rep_dog.dog_short;
DELETE FROM rr_rep_dz_kz;
INSERT INTO rr_rep_dz_kz ( kod_dog -- , ym , kod_Sf , nom_sf -- полный номер сф (номер и дата) , nachisl -- начислено с ндс , nal -- начислено налога , nachisl_re -- выставлен сф с ндс , nal_re -- выставлен налог по сф ) WITH sf as ( -- выставленные СФ и налог по ним SELECT dp.kod_dog , fv.ym , fr.rym as rym_max , max(fv.kod_sf) as kod_sf , max(fv.nom_sf || ' от ' || to_char(fv.dat_sf,'dd.mm.yyyy')) as nom_sf , SUM(nvl(fr.nachisl,0)) as nachisl_re , SUM(nvl(fr.nal,0)) as nal_re FROM sr_facvip fv INNER JOIN (select kod_sf , max(rym) rym , SUM(nvl(f.nachisl,0)) as nachisl , SUM(nvl(nal,0)) as nal FROM sr_facras f LEFT JOIN (SELECT SUM(nvl(nal,0)) as nal, kod_ras FROM sr_facras_nal GROUP BY kod_ras) n ON f.kod_ras = n.kod_ras LEFT JOIN sk_nachisl n ON f.vid_t = n.vid_t LEFT JOIN sk_vid_real vr ON n.vid_real = vr.vid_real WHERE f.price is not null AND NOT (nvl(f.price,0) = 0 and f.vid_t in (22,42,62,72)) AND vr.pr_osn = 1 -- основная реализация GROUP BY f.kod_sf ) fr ON fv.kod_sf = fr.kod_sf INNER JOIN kr_dogovor d ON fv.kod_dog=d.kod_dog INNER JOIN kr_dogovor dp On dp.kod_dog = d.kod_dog_fin WHERE fv.ym = :p_ym AND fv.pr_osn=1 AND dp.dep = :p_dep -- AND ((cnt_dog = 0) Or (cnt_dog > 0 And dp.kod_dog IN (SELECT val FROM vr_number_array WHERE array_id = 'p_kod_dog'))) { AND dp.kod_dog IN :p_kod_dog } GROUP BY dp.kod_dog, ym, rym ) , nach AS ( -- начисления SELECT kod_dog as kod_dog_acc , nvl(rym, ym) as rym_acc , ym as ym_acc , sum(nachisl) as nachisl_acc , sum(nal) as nal_acc FROM nv_account WHERE ym = :p_ym AND vid_real=2 AND dep = :p_dep -- AND ((cnt_dog = 0) Or (cnt_dog > 0 And kod_dog IN (SELECT val FROM vr_number_array WHERE array_id = 'p_kod_dog'))) { AND kod_dog IN :p_kod_dog } GROUP BY kod_dog, ym, nvl(rym, ym) ) SELECT sf.kod_dog , sf.kod_Sf , sf.nom_sf , nach.nachisl_acc as nachisl , nach.nal_acc as nal , sf.nachisl_re as nachisl_re , sf.nal_re as nal_re FROM sf LEFT JOIN nach ON nach.kod_dog_acc = sf.kod_dog and nach.rym_acc = sf.rym_max ;
END;
