report.procedure:
---- отчет № 21 DECLARE s_data_pr_id rr_temp.skod%type; s_data_byt_id rr_temp.skod%type; BEGIN s_data_pr_id :='3d8b60b1-74610-gr_pr-8c99c9d294fb'; s_data_byt_id :='3d8b60b1-74610-gr_byt-8c99c9d294fb'; DELETE FROM rr_temp WHERE skod in (s_data_pr_id, s_data_byt_id);
INSERT INTO rr_temp (skod, n1, -- номер группы s1, -- наименование группы s2, -- наименование потребителей n2, -- фактический УН (код) s3, -- фактический УН (наименование) n3, -- номер для сортировки УН n4, -- edizm s4, -- edizm_name n5, -- полезный отпуск cust n6 -- cust кВтч ) -- Услуги по передаче (по составляющей = 2) по группе потребителей прочие select s_data_pr_id , gr_num -- номер группы (1- одноставочные, 2 - двухставочные) , max(CASE WHEN gr_num = 1 THEN 'Одноставочный тариф на услуги по передаче' ELSE 'Двухставочный тариф на услуги по передаче' END) as gr_name , stragg_dist(payer_name) as payer_name -- наименование потребителей , voltage_fact , max(v.abbr) as voltage_fact_name , max(v.kod_sort) as volt_kod_sort , a.edizm , max(e.name) as edizm_name , sum(COALESCE(a.cust,0)) as cust , sum(COALESCE(CASE WHEN a.edizm = 4 THEN a.cust ELSE 0 END,0)) as cust_in_gr -- ПО кВтч from ( SELECT CASE WHEN kg_tarif.is_2rate(kg_tarif.subtarif_voltage(b.tarif, a.voltage, a.dat_priem - 1E-5), a.dat_priem - 1E-5) = 1 THEN 2 ELSE 1 END gr_num , p.name as payer_name , COALESCE(HG_CONTROLPOINT_R.get_factVoltage_byNumObj(a.kod_numobj,a.ym), a.voltage) as voltage_fact , a.voltage , s.edizm , COALESCE(b.cust,0) as cust FROM nr_account a LEFT JOIN (select kod_account, max(tip_tarif_sost) as tip_tarif_sost , max(tarif) as tarif , SUM(cust) as cust from nr_account_sost where tip_tarif_sost = 2 group by kod_account ) b ON a.kod_account = b.kod_account INNER JOIN hr_reglament_pp p ON a.kod_reglament=p.kod_reglament INNER JOIN hr_calc_variant v ON v.kod_variant=p.kod_variant INNER JOIN sk_nachisl s ON a.vid_t=s.vid_t INNER JOIN kr_dogovor d ON a.kod_dog = d.kod_dog INNER JOIN kr_dogovor dp ON d.kod_dog_fin = dp.kod_dog INNER JOIN ks_vdog vd ON dp.kod_vdog = vd.kod_vdog LEFT JOIN kr_numobj kr_n ON a.kod_numobj = kr_n.kod_numobj LEFT JOIN kr_object ob ON kr_n.kod_obj = ob.kod_obj LEFT JOIN kr_payer p ON dp.kodp = p.kodp -- потребитель LEFT JOIN TABLE (HG_SALEPOINT_R.PL_getSLPOrgs (kod_numobj_ => a.kod_numobj, ym_ => a.ym)) pc ON (a.kod_numobj = pc.kod_numobj) LEFT JOIN kr_payer tco ON (pc.kodp_pwrcompany = tco.kodp) WHERE 1=1 { AND dp.dep IN :p_dep} { AND a.ym = :p_ym} and tco.kodp = 27348 -- отчет только по Рязанской ТСО AND v.kod_typevariant = 1 -- покупка AND (b.tip_tarif_sost = 2 ) -- составляющая по услуге по передаче AND s.edizm in (3,4) AND a.price IS not null AND a.rym is null AND ((dp.pr_active in (0,2) AND dp.dat_fin IS NULL) OR kg.kf_ym(dp.dat_fin) >= :p_ym) AND vd.kod_vdoc=1 and vd.kod_tipdog=0 ) a INNER JOIN hk_voltage v ON a.voltage_fact = v.voltage INNER JOIN sk_edizm e ON a.edizm = e.edizm GROUP BY gr_num, voltage_fact, a.edizm ;
-- для населения отдельный запрос для данного отчета. Пока что достаем одну цифру. Алгоритма анализа групп диапазонов пока нет. INSERT INTO rr_temp (skod, n1, -- номер группы -- n4, -- edizm -- s4, -- edizm_name n5 -- полезный отпуск cust ) SELECT s_data_byt_id , 3 as gr_num -- номер группы -- , s.edizm -- , e.name as edizm_name , SUM(COALESCE(a.cust,0)) as cust FROM nr_account a INNER JOIN hr_reglament_pp p ON a.kod_reglament=p.kod_reglament INNER JOIN hr_calc_variant v ON v.kod_variant=p.kod_variant INNER JOIN sk_nachisl s ON a.vid_t=s.vid_t INNER JOIN kr_dogovor d ON a.kod_dog = d.kod_dog INNER JOIN kr_dogovor dp ON d.kod_dog_fin = dp.kod_dog LEFT JOIN kr_numobj kr_n ON a.kod_numobj = kr_n.kod_numobj LEFT JOIN kr_object ob ON kr_n.kod_obj = ob.kod_obj LEFT JOIN kr_payer p ON dp.kodp = p.kodp -- потребитель LEFT JOIN TABLE (HG_SALEPOINT_R.PL_getSLPOrgs (kod_numobj_ => a.kod_numobj, ym_ => a.ym)) pc ON (a.kod_numobj = pc.kod_numobj) LEFT JOIN kr_payer tco ON (pc.kodp_pwrcompany = tco.kodp) INNER JOIN sk_edizm e ON s.edizm = e.edizm WHERE 1=1 { AND dp.dep IN :p_dep} { AND a.ym = :p_ym} and tco.kodp = 27348 -- отчет только по Рязанской ТСО AND v.kod_typevariant = 1 /*покупка*/ AND s.edizm = 4 -- in (3,4) AND a.price IS not null AND a.rym is null AND ((dp.pr_active in (0,2) AND dp.dat_fin IS NULL) OR kg.kf_ym(dp.dat_fin) >= :p_ym) AND Decode(nvl(a.tarif,0), 0, 0, kg_tarif.f_byt_cached(a.tarif)) <> 0 ; -- население определяем по тарифу byt END;
