report.procedure:
-- Отчет № 1067. SD: 75041. Рязань. ГП. DECLARE s_data_id rr_temp.skod%type; cnt_dep number; -- кол-во выбранных отделений cnt_gr_cust number; -- кол-во выбранных гр. потребителей эл.энергии cnt_dog number; -- кол-во выбранных договоров cnt_tco number; -- кол-во выбранных ТСО p_date date; BEGIN s_data_id :='3d8b60b1-75041-ac16-8c99c9d294fb';
p_date := :p_date; delete vr_number_array; INSERT INTO vr_number_array (array_id,val) SELECT 'p_dep', kodp FROM kr_org WHERE kodp=:p_dep; INSERT INTO vr_number_array (array_id,val) SELECT 'p_gr_cust', kod_group_cust FROM ks_group_cust WHERE 1=0 { OR kod_group_cust in :p_gr_cust}; INSERT INTO vr_number_array (array_id,val) SELECT 'p_dog', kod_dog FROM kr_dogovor WHERE 1=0 { OR kod_dog in :p_dog}; INSERT INTO vr_number_array (array_id,val) SELECT 'p_tco', kodp FROM kr_payer WHERE 1=0 { OR kodp in :p_tco}; cnt_dep := ng_rep_common.get_count_value_param('p_dep'); cnt_gr_cust := ng_rep_common.get_count_value_param('p_gr_cust'); cnt_dog := ng_rep_common.get_count_value_param('p_dog'); cnt_tco := ng_rep_common.get_count_value_param('p_tco');
DELETE FROM rr_temp WHERE skod = s_data_id;
INSERT INTO rr_temp (skod, n9, n1, -- gr_num_obj - номер группы потребителя (на объекте. hs_23) s1, -- gr_name_obj - наименование группы потребителя на объекте n2, -- sub_gr_num - номер подгруппы в отчете (1 или 2) s2, -- sub_gr_name - наименование подгруппы потребителя в отчете (Для группы 1: 1- ИКУ, 2 - Иные потребители, приравненные к населению -- для группы 2: 1- 'менее 670 кВт' 2 - '670 кВт и выше' ) n3, -- kod_dog s3, -- ndog - номер договора s4, -- payer_name - наименование потребителя s5, -- gr_cust_name - категория потребителя (номер и наименование гр.потребителя на договоре из спр-ка ks_group_cust) n4, -- kod_numobj n5, -- n.num_obj - номер объекта (для сортировки) s6, -- obj_name - номер и наименование объекта s7, -- obj_adr_name - адрес объекта d1, -- obj_date_beg - дата постановки на учет объекта n6, -- max_power - максимальная мощность на объекте (кВт) n7, -- slpnt_cnt - кол-во ТП (точек поставки) n8 -- pnt_cnt - кол-во ТН (точек учета) ) WITH slpnt AS (/*Точки поставки Активных, Новых объектов на заданную дату (sysdate) плюс max мощность, гр.потребителей на объекте*/ SELECT h.kod_numobj , MAX(COALESCE(HG_DOGR.get_PMAX_NUMOBJ(n.kod_numobj, 2, p_date, 0,null),0)) as max_power , MAX(CASE WHEN COALESCE(n.kod_f23,0) = 38 THEN 1 ELSE 2 END) as gr_num_obj -- номер группы на объекте , MAX(CASE WHEN COALESCE(n.kod_f23,0) = 38 THEN 'Население и приравненные к нему категории потребителей' ELSE 'Прочие потребители' END) as gr_name_obj , COUNT(COALESCE(KOD_PMAX_DETAIL,0)) as slpnt_cnt FROM kr_dogovor d INNER JOIN kr_numobj n ON d.kod_dog = n.kod_dog LEFT JOIN hr_pmax_history h ON n.kod_numobj = h.kod_numobj AND p_date /*to_date('23.03.2022','DD.MM.YYYY')*/ BETWEEN h.dat_s AND COALESCE (h.dat_po, SYSDATE + 5000) LEFT JOIN hr_pmax_detail hd ON h.kod_pmax_history = hd.kod_pmax_history LEFT JOIN hr_attpoint a ON (hd.kod_attpoint = a.kod_attpoint AND hd.kodnagruzpotreb = a.kodnagruzpotreb) LEFT JOIN kr_payer net ON a.kodp_net = net.kodp -- LEFT JOIN hs_23 ON n.kod_f23 = hs_23.kod_f23 WHERE (cnt_dep = 0 OR d.dep IN (SELECT val FROM vr_number_array WHERE array_id = 'p_dep')) AND (cnt_dog = 0 OR d.kod_dog IN (SELECT val FROM vr_number_array WHERE array_id = 'p_dog')) AND (cnt_tco = 0 OR net.kodp IN (SELECT val FROM vr_number_array WHERE array_id = 'p_tco')) GROUP BY h.kod_numobj ) , pnt AS ( /*Точки учета Активных, Новых объектов на заданную дату (sysdate)*/ SELECT kod_numobj, COUNT(KOD_POINT) pnt_cnt FROM hv_point p WHERE p_date /*to_date('23.03.2022','DD.MM.YYYY') */ BETWEEN p.dat_s AND COALESCE (p.dat_po, SYSDATE + 5000) AND (cnt_dep = 0 OR p.dep IN (SELECT val FROM vr_number_array WHERE array_id = 'p_dep')) GROUP BY kod_numobj ) , pre AS (SELECT s_data_id , gr_num_obj , gr_name_obj , CASE WHEN slpnt.gr_num_obj = 1 THEN (CASE WHEN gc.num_gr in ('2.0', '2.3', '4.0', '4.2') THEN 1 ELSE 2 END) -- gc.num_gr - гр. потребителей на договоре для группировки ELSE (CASE WHEN slpnt.max_power < 670 THEN 1 ELSE 2 END) END as sub_gr_num , CASE WHEN slpnt.gr_num_obj = 1 THEN (CASE WHEN gc.num_gr in ('2.0', '2.3', '4.0', '4.2') THEN 'Исполнители коммунальных услуг' ELSE 'Иные потребители, приравненные к населению' END) ELSE (CASE WHEN slpnt.max_power < 670 THEN 'менее 670 кВт' ELSE '670 кВт и выше' END) END as sub_gr_name , d.kod_dog , d.ndog , p.name as payer_name , gc.num_gr || ' - ' || gc.name as gr_cust_name -- категория потребителя (номер и наименование гр.потребителя на договоре из спр-ка ks_group_cust) , n.kod_numobj , n.num_obj , '(' || d.ndog || '.' || to_char(n.num_obj,'000') ||') ' || n.name as obj_name , substr(nk_adress.kf_address(2,ob.kodd),26,250) as obj_adr_name -- адрес должен быть без области и города , n.dat_create as obj_date_beg , slpnt.max_power , COALESCE(slpnt_cnt, 0) as slpnt_cnt , COALESCE(pnt_cnt,0) as pnt_cnt FROM kr_dogovor d INNER JOIN kr_dogovor_dop dd ON d.kod_dog = dd.kod_dog -- INNER JOIN kv_group_cust_level gcc ON dd.kod_group_cust = gcc.kod_group_cust LEFT JOIN ks_group_cust gc ON dd.kod_group_cust = gc.kod_group_cust INNER JOIN kr_numobj n ON d.kod_dog = n.kod_dog LEFT JOIN kr_object ob ON n.kod_obj = ob.kod_obj LEFT JOIN kr_payer p ON d.kodp = p.kodp LEFT JOIN slpnt ON n.kod_numobj = slpnt.kod_numobj LEFT JOIN pnt ON n.kod_numobj = pnt.kod_numobj
WHERE p_date /*to_date('23.03.2022','DD.MM.YYYY')*/ BETWEEN COALESCE(n.dat_postav_obj, n.dat_create) AND COALESCE(n.dat_fin, SYSDATE + 5000) AND (cnt_dep = 0 OR d.dep IN (SELECT val FROM vr_number_array WHERE array_id = 'p_dep')) AND (cnt_gr_cust = 0 OR dd.kod_group_cust IN (SELECT val FROM vr_number_array WHERE array_id = 'p_gr_cust')) AND (cnt_dog = 0 OR d.kod_dog IN (SELECT val FROM vr_number_array WHERE array_id = 'p_dog')) )
SELECT s_data_id , ROW_NUMBER() OVER (ORDER BY gr_num_obj NULLS LAST, sub_gr_num, (lpad(ndog, 10 , '0')), num_obj) as n9 , gr_num_obj , gr_name_obj , sub_gr_num , sub_gr_name , kod_dog , ndog , payer_name , gr_cust_name -- категория потребителя (номер и наименование гр.потребителя на договоре из спр-ка ks_group_cust) , kod_numobj , num_obj , obj_name , obj_adr_name -- адрес должен быть без области и города , obj_date_beg , max_power , slpnt_cnt , pnt_cnt FROM pre ; END;
