report.procedure:
BEGIN
delete rr_rep_po;
INSERT INTO rr_rep_po (kod_dog, ndog, payer_name, opl, price, nal, price_gp) WITH gp as ( SELECT b.kod_dog, b.money/b.cust_kvth as price_gp FROM (SELECT dp.kod_dog, SUM(a.money) as money, SUM(COALESCE(CASE WHEN a.edizm = 4 THEN a.cust ELSE 0 END,0)) as cust_kvth FROM nv_account a INNER JOIN hr_reglament_pp r ON a.kod_reglament=r.kod_reglament INNER JOIN hr_calc_variant v ON v.kod_variant=r.kod_variant INNER JOIN kr_dogovor dp ON a.kod_dog_fin = dp.kod_dog WHERE dp.dep = :p_dep AND a.ym = kg.ym_add(:p_ym,-1) -- цена ГП за предыдущий период AND v.kod_typevariant = 2 -- 2 - цены ГП AND a.price IS not null GROUP BY dp.kod_dog ) b ) , pre as (SELECT dp.kod_dog, max(dp.ndog) as ndog, max(p.name) as payer_name, sum(COALESCE(o.opl,0)) as opl, max(fr.price) as price, max(n.nal) as nal FROM sr_opl o INNER JOIN sr_facvip fv ON o.kod_sf = fv.kod_Sf INNER JOIN sr_facras fr ON fv.kod_sf = fr.kod_sf INNER JOIN kr_dogovor d ON fv.kod_dog = d.kod_dog INNER JOIN kr_dogovor dp ON d.kod_dog_fin = dp.kod_dog INNER JOIN sk_vid_real vr ON fv.vid_real = vr.vid_real -- pr_osn нужно брать из вида реализации для определения основной реализации LEFT JOIN (SELECT SUM(nvl(nal,0)) as nal, kod_ras FROM sr_facras_nal GROUP BY kod_ras) n ON fr.kod_ras = n.kod_ras LEFT JOIN kr_payer p ON dp.kodp = p.kodp WHERE vr.pr_osn=1 AND dp.dep= :p_dep AND o.ym = :p_ym GROUP BY dp.kod_dog ) SELECT pre.kod_dog, pre.ndog, pre.payer_name, pre.opl, pre.price, pre.nal, COALESCE(gp.price_gp,0) as price_gp FROM pre LEFT JOIN gp ON pre.kod_dog = gp.kod_dog ;
END;
