report.procedure:
-- Рязань.ГП. SD: 75680. Отчет № 1058. Сведения о начислении и оплате. begin DELETE rr_rep_dog; INSERT INTO rr_rep_dog ( kod_dog , ndog , payer_id -- ID потребителя , payer_name -- наименование потребителя ) SELECT d.kod_dog , d.ndog , p.kodp as payer_id , p.name as payer_name FROM kr_dogovor d LEFT JOIN kr_payer p ON d.kodp = p.kodp WHERE d.kod_dog = :p_kod_dog;
DELETE rr_rep_dz_kz_dog; INSERT INTO rr_rep_dz_kz_dog (kod_dog , ym , saldo_beg , cust_kvth_in_period , nachisl_osn_in_period , opl_all_in_period , nachisl_pe_in_period , opl_pe_in_period , saldo_end ) WITH pre_nachislenie as ( SELECT dp.kod_dog , fv.ym , SUM(CASE WHEN vr.pr_osn = 1 THEN COALESCE(fr.nachisl,0) ELSE 0 END) as nachisl_osn_in_period -- начислено по осн.реализации в периоде , SUM(CASE WHEN vr.pr_osn = 1 AND fr.edizm = 4 THEN COALESCE(fr.cust,0) ELSE 0 END) as cust_kvth_in_period -- начислено объем э/э по осн.реализации в периоде , SUM(CASE WHEN fv.vid_real = 7 THEN COALESCE(fr.nachisl,0) ELSE 0 END) as nachisl_pe_in_period -- начислено пеней в периоде FROM sr_facvip fv LEFT JOIN sr_facras fr ON fv.kod_sf = fr.kod_sf LEFT JOIN kr_dogovor d ON fv.kod_dog = d.kod_dog LEFT JOIN kr_dogovor dp ON d.kod_dog_fin = dp.kod_dog LEFT JOIN sk_vid_real vr ON fv.vid_real = vr.vid_real -- pr_osn нужно брать из вида реализации для определения основной реализации WHERE fv.ym <= :p_ym_end -- < p_ym_end_next AND fv.pr_osn = 1 AND dp.kod_dog = :p_kod_dog GROUP BY dp.kod_dog, fv.ym )
, nachislenie as (select kod_dog , ym , lag(nachisl_osn_in_period,1,0) over(order by ym) as nachisl_lag , nachisl_osn_in_period , cust_kvth_in_period , nachisl_pe_in_period from pre_nachislenie )
, pre_opl as ( SELECT dp.kod_dog , o.ym , SUM(CASE WHEN vr.pr_osn = 1 AND o.kod_type_opl IN (0,3,4) THEN COALESCE(o.opl,0) ELSE 0 END) as opl_in_ym -- оплата ДЗ в периоде , SUM(CASE WHEN o.vid_real = 7 AND o.kod_type_opl IN (0,3,4) THEN COALESCE(o.opl,0) ELSE 0 END) as opl_pe_in_period -- оплата пеней в периоде , SUM(CASE WHEN o.vid_real <> 1 THEN (COALESCE(o.opl,0) + COALESCE(o.opls,0)) ELSE 0 END) As opl_all_in_period -- оплачено всего за месяц , SUM(CASE WHEN o.vid_real < 7 AND o.vid_real <> 1 AND o.kod_type_opl IN (1,2,5,6) THEN COALESCE(o.opl,0) + COALESCE(o.opls,0) ELSE 0 END) As kz_ym -- оплата по КЗ FROM sr_opl o INNER JOIN kr_dogovor d ON o.kod_dog = d.kod_dog INNER JOIN kr_dogovor dp ON d.kod_dog_fin = dp.kod_dog INNER JOIN sk_vid_real vr ON o.vid_real = vr.vid_real -- pr_osn нужно брать из вида реализации для проверки основной реализации WHERE o.ym <= :p_ym_end AND dp.kod_dog = :p_kod_dog GROUP BY dp.kod_dog, o.ym ) , opl as (SELECT kod_dog , ym , opl_in_ym , kz_ym , lag(opl_in_ym,1,0) over(order by ym) as opl_in_ym_lag , lag(kz_ym,1,0) over(order by ym) as kz_ym_lag , opl_all_in_period , opl_pe_in_period FROM pre_opl )
, pre as ( SELECT n.kod_dog , n.ym , sum(n.nachisl_lag) over(order by n.ym) as nachisl_beg , sum(o.opl_in_ym_lag) over(order by o.ym) as opl_beg , n.nachisl_osn_in_period , n.cust_kvth_in_period , n.nachisl_pe_in_period , sum(o.kz_ym_lag) over(order by o.ym) as kz_beg , COALESCE(opl_all_in_period,0) as opl_all_in_period , COALESCE(opl_pe_in_period,0) as opl_pe_in_period FROM nachislenie n LEFT JOIN opl o ON n.kod_dog=o.kod_dog AND n.ym = o.ym )
SELECT kod_dog , ym , nachisl_beg - opl_beg - kz_beg as saldo_beg , cust_kvth_in_period , nachisl_osn_in_period , opl_all_in_period , nachisl_pe_in_period , opl_pe_in_period , nachisl_beg - opl_beg - kz_beg + nachisl_osn_in_period - opl_all_in_period as saldo_end FROM pre WHERE ym >= :p_ym_beg ; END;
